    var maps = new ej.maps.Maps({

        zoomSettings: {
            enable: true,
            mouseWheelZoom: false,
            pinchZooming: false
        },
        useGroupingSeparator: true,
        format: 'n',
        titleSettings: {
            text: 'Sales details of products in various countries',
            textStyle: {
                size: '16px'
            }
        },
		tooltipDisplayMode: 'Click',
        layers: [
            {
                shapeData: new ej.maps.MapAjax('//ej2.syncfusion.com/javascript/demos/src/maps/map-data/world-map.json'),
                dataSource: window.salesmap,
                shapeSettings: {
                    fill: '#C1DFF5'
                },
                markerClusterSettings: {
                    allowClustering: true,
                    allowClusterExpand: true,
                    shape: 'Image',
                    height: 40,
                    width: 40,
                    labelStyle: { color: 'white' },
                    imageUrl: '//ej2.syncfusion.com/javascript/demos/src/maps/images/cluster.svg'
                },
                markerSettings: [
                    {
                        dataSource: window.salesmap,
                        visible: true,
                        animationDuration: 0,
                        height: 15,
                        width: 15,
                        shape: 'Image',
                        imageUrl: '//ej2.syncfusion.com/javascript/demos/src/maps/images/ballon.png',
                        tooltipSettings: {
                            format: '<b>Name</b> : ${name}<br><b>Product</b> : ${product}<br><b>Total value</b> : ${worth}',
                            visible: true,
                            valuePath: 'name',
                        }
                    },
                ]
            }
        ]
    });
    maps.appendTo('#container');
